"""Test number formatting functionality"""from xls2tex.xlWB import xlWorkbookTeXimport xls2tex.e2lvp as e2lvpif __name__ == '__main__':        wb = xlWorkbookTeX('tstnumfmt.xlsx')        for s in wb.sheetnames:        ws = wb[s]                sr, sc, er, ec = e2lvp._get_table_dimensions(ws)                for r in range(sr+1, er+2): # quick hack to obtain inclusive range, not robust             for c in range(sc+1, ec+2):                fc = e2lvp._reformat_cellval(ws.cell(row=r, column=c))                # print(f"({r},{c}) == {ws.cell(row=r, column=c).value} \t {ws.cell(row=r, column=c).number_format}", end="\t")                print(f"({r},{c}) == {fc} \t {ws.cell(row=r, column=c).number_format}", end="\t")            print()